                />
                <Link href='#'>
                  <TermConditionLink>
                    <FormattedMessage
                      id='termAndCondition'
                      defaultMessage='terms and conditions.'
                    />
                  </TermConditionLink>
                </Link>
              </TermConditionText>

              {/* CheckoutSubmit */}
              {checkoutError && (
                        <ErrorMsg>
                            <FormattedMessage
                                id='checkoutError'
                                defaultMessage={checkoutError}
                            />
                        </ErrorMsg>
                    )}

              <CheckoutSubmit>
                <Button
                    type='button'
                    onClick={handleSubmit}
                    disabled={loading}
                    size='big'
                    loading={loading}
                    style={{ width: '100%' }}
                >
                  <FormattedMessage
                    id={loading ? 'processesingCheckout' : 'processCheckout'}
                    defaultMessage='Proceed to Checkout'
                  />
                   . ({CURRENCY}{calculatePrice(calculateDeliveryCharge(deliveryMethodSelected?.name)+calculateCCCharge())})
                </Button>
              </CheckoutSubmit>
                <div>

                    {orderError && (
                        <ErrorMsg>
                            <p>{orderError}</p>
                        </ErrorMsg>
                    )}
                </div>
            </InformationBox>
          </CheckoutInformation>

          <CartWrapper>
            {/* <Sticky enabled={true} top={totalHeight} innerZ={999}> */}
            <Sticky
              enabled={size.width >= 768 ? true : false}
              top={120}
              innerZ={999}
            >
              <OrderInfo>
                <Title>
                  <FormattedMessage
                    id='cartTitle'
                    defaultMessage='Your Orderdfd'
                  />
                </Title>

                <Scrollbars
                  universal
                  autoHide
                  autoHeight
                  autoHeightMax='390px'
                  renderView={(props) => (
                    <div
                      {...props}
                      style={{
                        ...props.style,
                        marginLeft: isRtl ? props.style.marginRight : 0,
                        marginRight: isRtl ? 0 : props.style.marginRight,
                        paddingLeft: isRtl ? 15 : 0,
                        paddingRight: isRtl ? 0 : 15,
                      }}
                    />
                  )}
                >
                  <ItemsWrapper>
                    {cartItemsCount > 0 ? (
                      items.map((item) => (
                        <OrderItem key={`cartItem-${item.id}`} product={item} />
                      ))
                    ) : (
                      <>
                        <NoProductImg>
                          <NoCartBag />
                        </NoProductImg>

                        <NoProductMsg>
                          <FormattedMessage
                            id='noProductFound'
                            defaultMessage='No products found'
                          />
                        </NoProductMsg>
                      </>
                    )}
                  </ItemsWrapper>
                </Scrollbars>

                <CalculationWrapper>
                  <TextWrapper>
                    <Text>
                      <FormattedMessage id='subTotal' defaultMessage='Subtotal' />
                    </Text>
                    <Text>
                      {CURRENCY} {calculateSubTotalPrice()}
                    </Text>
                  </TextWrapper>

                  <TextWrapper>
                    <Text>
                      <FormattedMessage id='discountText' defaultMessage='Discount' />
                    </Text>
                    <Text>
                      {CURRENCY} {calculateDiscount()}
                    </Text>
                  </TextWrapper>

                  {calculateCCCharge() > 0 && (
                    <TextWrapper>
                      <Text>
                        <FormattedMessage id='ccrChargeText' defaultMessage='Card charge' />
                      </Text>
                      <Text>
                        {CURRENCY} {calculateCCCharge()?.toFixed(2)}
                      </Text>
                    </TextWrapper>
                  )}

                  <TextWrapper>
                    <Text>
                      <FormattedMessage id='deliveryChargeText' defaultMessage='Delivery charge' />
                    </Text>
                    <Text>
                      {CURRENCY} {calculateDeliveryCharge(deliveryMethodSelected?.name)}
                    </Text>
                  </TextWrapper>

                  <TextWrapper style={{ marginTop: 20 }}>
                    <Bold>
                      <FormattedMessage id='totalText' defaultMessage='Total' />{' '}
                    </Bold>
                    <Bold>
                      {CURRENCY} {calculatePrice(calculateDeliveryCharge(deliveryMethodSelected?.name)+calculateCCCharge())}
                    </Bold>
                  </TextWrapper>
                </CalculationWrapper>
              </OrderInfo>
            </Sticky>
          </CartWrapper>
        </CheckoutContainer>
      </CheckoutWrapper>
    </form>
  );
};

export default CheckoutWithSidebar;
