import React, { useState, useEffect, useRef, useContext } from 'react';
import { BannerIcon, BannerText, DeliveryMethods, LocationContent, MethodOption, SubHeaderWrapper } from './sub-header.style';
import { useLocale } from 'contexts/language/language.provider';
import DeliveryIcon from 'assets/images/locationIcon.webp';
import Popover from 'components/popover/popover';
import { useMedia } from 'utils/use-media';
import { MenuItem } from 'layouts/header/menu/language-switcher/language-switcher.style';
import { FormattedMessage } from 'react-intl';
import PopoverBigger from 'components/popover-bigger/popover-bigger';
import LocationModal from 'features/location-modal/location-modal';
import { AuthContext } from 'contexts/auth/auth.context';
interface Props {
  className: string
}


const LocationMenu = ({ cityAndStateText, deliveryMethodSelected }) => {

  return (
    <LocationContent>
        <i class="ld ld-ChevronUp ml2 dn db-m" aria-hidden="true"></i>
        {deliveryMethodSelected}
        |
        <BannerIcon><img src={DeliveryIcon} alt="" /></BannerIcon>
        <BannerText>{cityAndStateText && cityAndStateText}</BannerText>
      </LocationContent>
  );
};

const DeliveryMethodsView = ({ cityAndStateText, setDeliveryMethod, openLocationModal }) => {
  return (
    <DeliveryMethods>
      <div onClick={() => openLocationModal()}>Find the closest DittoFarm</div>
      <MethodOption onClick={() => setDeliveryMethod('Pickup')}>Pickup</MethodOption> 
      <MethodOption onClick={() => setDeliveryMethod('Delivery')}>Delivery</MethodOption> 
    </DeliveryMethods>
  );
};

const SubHeader: React.FC<Props> = ({ ...props  }) => {
  const { location } = useLocale();
  const mobile = useMedia('(max-width: 580px)');
  const [deliveryMethodSelected, setDeliveryMethod] = React.useState('');
  const { authDispatch } = useContext<any>(AuthContext);

  const cityAndStateText = location ? mobile ?
    `${location.split(', ')[1]}, ${location.split(', ')[2]}`
    : `${location.split(', ')[0]}, ${location.split(', ')[1]}`
    : '';

  const openLocationModal = () => {
    console.log('setDeliveryMethod')
    authDispatch({
      type: 'LOCATION_MODAL',
    });
  };

  console.log('location',location)
  return (
    <SubHeaderWrapper className={props.className} id="layout-header">
      <PopoverBigger
        direction="left"
        className="user-pages-dropdown"
        handler={<LocationMenu deliveryMethodSelected={deliveryMethodSelected} cityAndStateText={cityAndStateText} />}
        content={<DeliveryMethodsView setDeliveryMethod={setDeliveryMethod} cityAndStateText={cityAndStateText} openLocationModal={openLocationModal} />}
      />
  </SubHeaderWrapper>
  );

};

export default SubHeader;
