import React from 'react';
import Table from 'rc-table';
import Collapse, { Panel } from 'rc-collapse';
import ReactToPrint from "react-to-print";
import Progress from 'components/progress-box/progress-box';

import { FormattedMessage, useIntl } from 'react-intl';

import {
  OrderListHeader,
  TrackID,
  Status,
  OrderMeta,
  Meta,
  CardWrapper,
  OrderDetail,
  DeliveryInfo,
  DeliveryAddress,
  Address,
  Contact,
  CostCalculation,
  PriceRow,
  Price,
  ProgressWrapper,
  OrderTable,
  OrderTableMobile,
} from './order-card.style';

import { CURRENCY } from 'utils/constant';
import moment from 'moment';
import { date } from 'yup';

type MobileOrderCardProps = {
  orderId?: any;
  onClick?: (e: any) => void;
  className?: any;
  status?: any;
  date?: any;
  deliveryTime?: any;
  amount?: number;
  tableData?: any;
  columns?: any;
  progressData?: any;
  progressStatus?: any;
  address?: string;
  subtotal?: number;
  discount?: number;
  deliveryFee?: number;
  grandTotal?: number;
  orders?: any;
};

const components = {
  table: OrderTable,
};

const OrderCard: React.FC<MobileOrderCardProps> = ({
  onClick,
  className,
  columns,
  progressData,
  orders,
}) => {
  //   const displayDetail = className === 'active' ? '100%' : '0';
  const addAllClasses: string[] = ['accordion'];
  const intl = useIntl();
  if (className) {
    addAllClasses.push(className);
  }
  console.log("orderrs:L", orders)

  const getDeliveryDate = (orderDate: string) => {
    moment.locale('es');
    let orderDay = moment(new Date(orderDate), 'MM/D/YYYY').day();
    const orderHour = moment(new Date(orderDate), 'MM/D/YYYY').hour();
    const orderMinute = moment(new Date(orderDate), 'MM/D/YYYY').minutes();
    let deliveryDate = moment(new Date(orderDate), 'MM/D/YYYY');
    const lastOrderHour = 14;
    const lastOrderMinute = 0;
    const lunes = 1;
    const martes = 2;
    const miercoles = 3;
    const jueves = 4;
    const viernes = 5;
    const sabado = 6;
    const domingo = 7;

    // case between jueves 14:00 and martes 13:59
    if ((orderDay >= jueves && orderHour > 13  ) && (orderDay <= martes && orderHour < 14)) {

      let deliveryDate = moment(new Date(orderDate));

      while (orderDay === martes) {
        orderDay = orderDay === domingo ? 1 : orderDay++;
        deliveryDate.add(1, 'days');
      }

      return deliveryDate;
    }
    // case between martes 14:01 and jueves 13:59
    if ((orderDay >= martes && orderHour > 13) && (orderDay < jueves || (orderDay === jueves && orderHour < 14))) {

      while (++orderDay === jueves) {
        // orderDay++;
        deliveryDate.add(1, 'days');
      }

      return moment(deliveryDate).format('hh:mm A - dddd DD MMM');
    }
    // order.delivery_address ? order.deliveryTime
  }

  return (
    <>
      <Collapse
        accordion={true}
        className={addAllClasses.join(' ')}
        defaultActiveKey="active"
        expandIcon={() => {}}
      >
        {orders.map((order: any) => (
          <Panel
            header={
              <CardWrapper onClick={onClick}>
                <OrderListHeader>
                  <TrackID>
                    Order <span>#{order.id}</span>
                  </TrackID>
                  <Status>{progressData[order.status - 1]}</Status>
                </OrderListHeader>

                <OrderMeta>
                  <Meta>
                    Order Date: <span>{moment(order.datetime).format('hh:mm A - DD MMM')}</span>
                  </Meta>
                  <Meta>
                  {intl.formatMessage({ id: 'deliveryTime', defaultMessage: 'Delivery Time:' })}<span>{getDeliveryDate(order.datetime)}</span>
                  </Meta>
                  <Meta className="price">
                    {intl.formatMessage({ id: 'totalPrice', defaultMessage: 'Total Price:' })}
                    <span>
                      {CURRENCY}
                      {order.total}
                    </span>
                  </Meta>
                </OrderMeta>
              </CardWrapper>
            }
            headerClass="accordion-title"
            key={order.id}
          >
            <OrderDetail>
              <DeliveryInfo>
                <DeliveryAddress>
                  <h3>Contact Number{intl.formatMessage({ id: 'totalPrice', defaultMessage: 'Total Price:' })}</h3>
                  <Contact>{order.contact_number}</Contact>
                </DeliveryAddress>
                <DeliveryAddress>
                  <h3>Delivery Address</h3>
                  <Address>{order.delivery_address}</Address>
                </DeliveryAddress>
                <CostCalculation>
                  <PriceRow>
                    Subtotal
                    <Price>
                      {CURRENCY}
                      {order.sub_total}
                    </Price>
                  </PriceRow>
                  <PriceRow>
                    Discount
                    <Price>
                      {CURRENCY}
                      {order.discount}
                    </Price>
                  </PriceRow>
                  <PriceRow className="grandTotal">
                    Total
                    <Price>
                      {CURRENCY}
                      {order.total}
                    </Price>
                  </PriceRow>
                </CostCalculation>
              </DeliveryInfo>

              {/*<ProgressWrapper>
                <Progress data={progressData} status={order.status} />
              </ProgressWrapper>

              <OrderTableMobile>
                <Table
                  columns={columns}
                  data={order.products}
                  rowKey={(record) => record.id}
                  components={components}
                  scroll={{ x: 450 }}
                  // scroll={{ y: 250 }}
                />
              </OrderTableMobile>*/}
            </OrderDetail>
          </Panel>
        ))}
      </Collapse>
    </>
  );
};

export default OrderCard;
