import React, { useState, useEffect, useRef, useContext } from 'react';
import { BannerIcon, BannerText, Button, CardWrapper, Container, DeliveryMethods, DeliveryText, Heading, Input, LocationContent, MethodOption, Offer, OfferSection, Options, SubHeaderWrapper, Wrapper } from './sub-header.style';
import { openModal } from '@redq/reuse-modal';
import { useLocale } from 'contexts/language/language.provider';
import DeliveryIcon from 'assets/images/locationIcon.webp';
import Popover from 'components/popover/popover';
import { useMedia } from 'utils/use-media';
import { MenuItem } from 'layouts/header/menu/language-switcher/language-switcher.style';
import { FormattedMessage } from 'react-intl';
import PopoverBigger from 'components/popover-bigger/popover-bigger';
import LocationModal from 'features/location-modal/location-modal';
import { AuthContext } from 'contexts/auth/auth.context';
import AuthenticationForm from 'features/authentication-form';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCheck, faChevronCircleDown, faChevronCircleUp, faTrash } from "@fortawesome/free-solid-svg-icons";
import { DeliveryMethodsConstants } from 'utils/constant';
import { useQuery } from '@apollo/react-hooks';
import { DELIVERY_METHOD } from 'graphql/query/delivery';
import { GiftBox } from 'assets/icons/GiftBox';
import Checkbox from 'components/checkbox/checkbox';

interface Props {
  className: string
}


const LocationMenu = ({ cityAndStateText, deliveryMethodTypeSelected, isOpen }) => {
  const noOptionSelected = deliveryMethodTypeSelected === DeliveryMethodsConstants.PICKUP || deliveryMethodTypeSelected === DeliveryMethodsConstants.DELIVERY;
  return (
    <LocationContent>
        { isOpen 
          ? (<FontAwesomeIcon icon={faChevronCircleDown} className="fas fa-chevron-down" style={{ color: "black" }} />) 
          : (<FontAwesomeIcon icon={faChevronCircleUp} className="fas fa-chevron-up" style={{ color: "black" }} />)
        }
        <DeliveryText>{deliveryMethodTypeSelected}</DeliveryText>
        { noOptionSelected && (
          <>
              |
            <BannerIcon><img src={DeliveryIcon} alt="" /></BannerIcon>
            <BannerText>{cityAndStateText && cityAndStateText}</BannerText>
          </>
        )}
      </LocationContent>
  );
};


const SubHeader: React.FC<Props> = ({ ...props  }) => {
  const { location } = useLocale();
  const mobile = useMedia('(max-width: 580px)');
  const [deliveryMethodTypeSelected, setDeliveryMethodType] = React.useState('How you want your items?');
  const [deliveryMethodSelected, setDeliveryMethod] = React.useState();
  const { data: deliverData } = useQuery(DELIVERY_METHOD)
  const { authDispatch } = useContext<any>(AuthContext);
  const [zipCode, setZipCode] = React.useState('');
  const [searchResult, setSearchResult] = React.useState([]);

  const setDeliveryMethodAndClose =  (deli) => {
    setDeliveryMethod(deli)
  };


  const setZipCodeAddress =  (e, deli) => {
    handleOnFocus(e)
    setZipCode(deli)

    searchZipCode(e, deli);
  };

  const searchZipCode =  (e, deli) => {
    const pickUpOptionsMethods = deliveryMethods?.filter(deliveryMethod => {
      return deliveryMethod.isPickUp === true;
    });

    const methodFound = pickUpOptionsMethods.filter(method => method.details.includes(zipCode));
    console.log(deli)

    e.stopPropagation();
    setSearchResult(deli ? methodFound : [])
  };
  console.log('deliveryMethodSelected',deliveryMethodSelected)
  const setDelivery =  (e, deli) => {
    e.stopPropagation();
    setDeliveryMethodType(deli)
  };

  const handleOnFocus =  (event) => {
    event.preventDefault()
    event.stopPropagation()
  };

  const deliveryMethods = deliverData?.deliveryMethods?.items;
  // const orderDeliveryMethod = deliveryMethods?.filter(method => method.id === myOrder?.delivery_method_id)[0];
  // const deliveryDateAndTime = `${myOrder?.delivery_pickup_date} ${getDeliverySchedule(orderDeliveryMethod?.details, intl.locale)}`;
  console.log('deliveryMethods', deliveryMethods)


  // deliveryMethodSelected
  const isPickUp = deliveryMethodTypeSelected === DeliveryMethodsConstants.PICKUP;
  const hasDefaultPickUpOption = true;
  const a = (isPickUp && hasDefaultPickUpOption) ? 'Find the closest PickUp Location' : 'Check delivery address ZIP CODE';
  const cityAndStateText = isPickUp ? a : location ? mobile ?
    `${location.split(', ')[1]}, ${location.split(', ')[2]}`
    : `${location.split(', ')[0]}, ${location.split(', ')[1]}`
    : '';

  return (
    <SubHeaderWrapper className={props.className} id="layout-header">
      <PopoverBigger
        direction="left"
        className="user-pages-dropdown"
        openHandler={<LocationMenu
          isOpen={false} deliveryMethodTypeSelected={deliveryMethodSelected} cityAndStateText={cityAndStateText} />
        }
        closeHandler={<LocationMenu
          isOpen={true} deliveryMethodTypeSelected={deliveryMethodTypeSelected} cityAndStateText={cityAndStateText}/>
        }
        content={
          <DeliveryMethods>

            <Options>
              <CardWrapper onClick={(e) => setDelivery(e, DeliveryMethodsConstants.PICKUP)}>Pickup</CardWrapper> 
              <CardWrapper onClick={(e) => setDelivery(e, DeliveryMethodsConstants.DELIVERY)}>Delivery</CardWrapper> 
            </Options>
            <Wrapper>
              <Container>
                <Heading  onClick={(e) => handleOnFocus(e)}>
                  { isPickUp ? (
                    <FormattedMessage
                      id="pickupModalheading"
                      defaultMessage="Select Your Location"
                    />
                  ) : (
                    <FormattedMessage
                      id="deliveryModalheading"
                      defaultMessage="Select Your Location"
                    />
                  )}
                </Heading>
                {/* <SubHeading>
                  <FormattedMessage
                    id="locationModalSubHeading"
                    defaultMessage="You have to select your location for deliver service perpous"
                  />
                </SubHeading> */}
                <div onClick={(e) => handleOnFocus(e)}>
                <Input
                    type='text'
                    name='name'
                    onFocus={(e) => handleOnFocus(e)}
                    placeholder='Enter zip code'
                    value={zipCode}
                    // we have to change the onChange because the is no one for the controller name actualy
                    onChange={(e) => setZipCodeAddress(e, e.target.value)}
                    backgroundColor='#F7F7F7'
                    width='197px'
                    height='34.5px'
                  />
                </div>
                {searchResult?.length ? searchResult.map((result, i) => {
                  // const isChecked = deliveryMethodSelected?.id === result.id;
                  return (
                      <Checkbox
                        keyName={`${i}-results`}
                        isChecked={deliveryMethodSelected?.id === result.id}
                        labelText={`${result.name} - ${result.details}`}
                        id={`result-${i}`}
                        onChange={e => {
                          setDeliveryMethod(deliveryMethodSelected?.id === result.id ? [] : result)
                        }}

                      />
                    )}
                  ) : ('')
                }
              </Container>
              {/* <Button
                  fullwidth
                  radius={100}
                  onClick={null}
                >
                  <FormattedMessage id='con' defaultMessage='Save' />
                </Button> */}
              <OfferSection>
                <GiftBox />
                <Offer>
                  <FormattedMessage
                    id="locationModalFooter"
                    defaultMessage="Free Delivery For 1st Order"
                    values={{ number: 1 }}
                  />
                </Offer>
              </OfferSection>
            </Wrapper>
        </DeliveryMethods>
        }
      />
  </SubHeaderWrapper>
  );

};

export default SubHeader;
