import React, { useContext, useState } from 'react';
import * as Yup from 'yup';
import { withFormik, FormikProps, Form } from 'formik';
import { closeModal } from '@redq/reuse-modal';
import TextField from 'components/forms/text-field';
import { Button } from 'components/button/button';
import { useMutation } from '@apollo/react-hooks';
import { UPDATE_ADDRESS } from 'graphql/mutation/address';
import { FieldWrapper, Heading } from './add-schedule-card.style';
import { ProfileContext } from 'contexts/profile/profile.context';
import { FormattedMessage, useIntl } from 'react-intl';
import Loader from 'components/loader/loader';

import TimePicker from 'react-time-picker'


// Shape of form values
interface FormValues {
  id?: number | null;
  name?: string;
  info?: string;
}

// The type of props MyForm receives
interface MyFormProps {
  item?: any | null;
}

// Wrap our form with the using withFormik HoC
const FormEnhancer = withFormik<MyFormProps, FormValues>({

  // Transform outer props into form values
  mapPropsToValues: (props) => {
    return {
      id: props.item.id || null,
      name: props.item.name || '',
      info: props.item.info || '',
    };
  },
  validationSchema: Yup.object().shape({
    name: Yup.string().required('Required!'),
    info: Yup.string().required('Required'),
  }),
  handleSubmit: (values) => {
    // do submitting things
  },
});

const AddTimeSchedule = (props: FormikProps<FormValues> & MyFormProps) => {
  const {
    isValid,
    item,
    values,
    touched,
    errors,
    dirty,
    handleChange,
    handleBlur,

    handleReset,
    isSubmitting,
  } = props;
  const addressValue = {
    id: values.id,
    type: 'secondary',
    name: values.name,
    info: values.info,
  };
  const { state, dispatch } = useContext(ProfileContext);
  const [loading, setLoading] = useState(false);
  const [value, onChange] = useState('10:00');
  const [addressMutation, { data }] = useMutation(UPDATE_ADDRESS);
  const intl = useIntl();

  const handleSubmit = async () => {
    if (isValid) {
      const addressData = await addressMutation({
        variables: { addressInput: JSON.stringify(addressValue) },
      });

      setLoading(true);
      dispatch({ type: 'ADD_OR_UPDATE_ADDRESS', payload: addressValue });
      closeModal();
      setLoading(false);
    }
  };

  // const onOpeningsChange = (e: any) => {
  //   setOpenings(e);
  //   console.log('openings::::::', e)
  // };

    // const weekDays = ['mon', 'tue', 'wed', 'thu', 'fri', 'sat', 'sun'];

    // const lightSchedule = {
    //     daysToRepeat: [weekDays[0], weekDays[2], weekDays[3]],
    //     startTime: '00:00',
    //     endTime: '23:59',
    //     enabled: true,
    //     smartLight: false
    // }

    // const b = {
    //     daysToRepeat: [weekDays[0], weekDays[2], weekDays[3]]
    // }
    // const onTimes = [a, b]


    //set notification schedule
    // [mon] tue wed thu ...
    // 00:00 --- 07:15
    // 15:15 ----- 23:59
    // Add time Schedule (button)

    // (when pressing the button you see this pop up)
    // Add time Schedule
    //start time: 00:00
    //end time: 23:59

    // repeat: (touch and you can see all days of the week to select/deselect) press ok, and original view is seeing the selected days


    // also the smart option for when is no light and there should be light
  return (
    <Form>
      <Heading>{<Heading>{intl.formatMessage({ id: 'addTimeScheduleId', defaultMessage: 'addTimeScheduleId' })}</Heading>}</Heading>

      <TimePicker
        onChange={onChange}
        value={value}
        className
      />

      <Button
        onClick={handleSubmit}
        type="submit"
        style={{ width: '100%', height: '44px' }}
        loader={<Loader />}
        isDisabled={loading}
        isLoading={loading}
      >
        <FormattedMessage id="savedAddressId" defaultMessage="Save Address" />
      </Button>
    </Form>
  );
};

export default FormEnhancer(AddTimeSchedule);
