import React, { useState } from 'react';
import {
  StyledCheckBox,
  StyledCheckBoxLabel,
  StyledCheckBoxLabelText,
  StyledCheckBoxInput,
  StyledCheckBoxInputIndicator,
} from './checkbox.style';

type CheckBoxProps = {
  id: string;
  disabled?: boolean;
  isChecked?: boolean;
  labelText: string;
  className?: string;
  labelPosition?: 'left' | 'right';
  [key: string]: unknown;
};

const CheckBox: React.FC<CheckBoxProps> = ({
  className,
  isChecked = false,
  labelText,
  id,
  labelPosition = 'right',
  disabled = false,
  ...props
}) => {
  // use toggle hooks
  const [checked, setChecked] = useState(isChecked);

  return (
    <StyledCheckBox className={`Mahdi Fashion__checkbox ${className}`.trim()}>
      <StyledCheckBoxLabel htmlFor={id} position={labelPosition}>

        <StyledCheckBoxInput
          type="checkbox"
          className="checkbox-input"

          checked={checked}
          onChange={() => setChecked((prev) => !prev)}
          disabled={disabled}
          {...props}
        />
        <StyledCheckBoxInputIndicator />
      </StyledCheckBoxLabel>
    </StyledCheckBox>
  );
};

export default CheckBox;
