import React, { useState, useEffect, useRef, useContext } from 'react';
import { BannerIcon, BannerText, DeliveryMethods, DeliveryText, LocationContent, MethodOption, Options, SubHeaderWrapper } from './sub-header.style';
import { openModal } from '@redq/reuse-modal';
import { useLocale } from 'contexts/language/language.provider';
import DeliveryIcon from 'assets/images/locationIcon.webp';
import Popover from 'components/popover/popover';
import { useMedia } from 'utils/use-media';
import { MenuItem } from 'layouts/header/menu/language-switcher/language-switcher.style';
import { FormattedMessage } from 'react-intl';
import PopoverBigger from 'components/popover-bigger/popover-bigger';
import LocationModal from 'features/location-modal/location-modal';
import { AuthContext } from 'contexts/auth/auth.context';
import AuthenticationForm from 'features/authentication-form';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCheck, faChevronCircleDown, faChevronCircleUp, faTrash } from "@fortawesome/free-solid-svg-icons";
import { DeliveryMethodsConstants } from 'utils/constant';
import { useQuery } from '@apollo/react-hooks';
import { DELIVERY_METHOD } from 'graphql/query/delivery';

interface Props {
  className: string
}


const LocationMenu = ({ cityAndStateText, deliveryMethodSelected, isOpen }) => {
  const noOptionSelected = deliveryMethodSelected === DeliveryMethodsConstants.PICKUP || deliveryMethodSelected === DeliveryMethodsConstants.DELIVERY;
  return (
    <LocationContent>
        { isOpen 
          ? (<FontAwesomeIcon icon={faChevronCircleDown} className="fas fa-chevron-down" style={{ color: "black" }} />) 
          : (<FontAwesomeIcon icon={faChevronCircleUp} className="fas fa-chevron-up" style={{ color: "black" }} />)
        }
        <DeliveryText>{deliveryMethodSelected}</DeliveryText>
        {/* { noOptionSelected && (
          <>
              |
            <BannerIcon><img src={DeliveryIcon} alt="" /></BannerIcon>
            <BannerText>{cityAndStateText && cityAndStateText}</BannerText>
          </>
        )} */}
      </LocationContent>
  );
};


const SubHeader: React.FC<Props> = ({ ...props  }) => {
  const { location } = useLocale();
  const mobile = useMedia('(max-width: 580px)');
  const [deliveryMethodSelected, setDeliveryMethod] = React.useState('How you want your items?');
  const { data: deliverData } = useQuery(DELIVERY_METHOD)
  const { authDispatch } = useContext<any>(AuthContext);
  const [subHeaderModal, setSubHeaderModal] = React.useState(false);

  const setDelivery =  (e, deli) => {
    e.stopPropagation();
    setDeliveryMethod(deli)
  };

  const deliveryMethods = deliverData?.deliveryMethods?.items;
  // const orderDeliveryMethod = deliveryMethods?.filter(method => method.id === myOrder?.delivery_method_id)[0];
  // const deliveryDateAndTime = `${myOrder?.delivery_pickup_date} ${getDeliverySchedule(orderDeliveryMethod?.details, intl.locale)}`;
  console.log('deliveryMethods', deliveryMethods)

  const pickUpOptionsMethods = deliveryMethods?.filter(deliveryMethod => {
    return deliveryMethod.isPickUp === true;
  });

  const isPickUp = deliveryMethodSelected === DeliveryMethodsConstants.PICKUP;
  const hasDefaultPickUpOption = true;
  const a = (isPickUp && hasDefaultPickUpOption) ? 'Find the closest PickUp Location' : 'Check delivery address ZIP CODE';
  const cityAndStateText = isPickUp ? a : location ? mobile ?
    `${location.split(', ')[1]}, ${location.split(', ')[2]}`
    : `${location.split(', ')[0]}, ${location.split(', ')[1]}`
    : '';

  const openLocationModal = () => {
    authDispatch({
      type: 'LOCATION_MODAL',
    });

    openModal({
      show: true,
      overlayClassName: 'quick-view-overlay',
      closeOnClickOutside: true,
      component: AuthenticationForm,
      closeComponent: '',
      config: {
        enableResizing: false,
        disableDragging: true,
        className: 'quick-view-modal',
        width: 458,
        height: 'auto',
      },
    });

  };

  return (
    <SubHeaderWrapper className={props.className} id="layout-header">
      <PopoverBigger
        direction="left"
        className="user-pages-dropdown"
        openHandler={<LocationMenu
          isOpen={false} deliveryMethodSelected={deliveryMethodSelected} cityAndStateText={cityAndStateText} />
        }
        closeHandler={<LocationMenu
          isOpen={true} deliveryMethodSelected={deliveryMethodSelected} cityAndStateText={cityAndStateText}/>
        }
        content={
          <DeliveryMethods>

            <Options>
              <MethodOption onClick={(e) => setDelivery(e, DeliveryMethodsConstants.PICKUP)}>Pickup</MethodOption> 
              <MethodOption onClick={(e) => setDelivery(e, DeliveryMethodsConstants.DELIVERY)}>Delivery</MethodOption> 
            </Options>
            { deliveryMethodSelected === DeliveryMethodsConstants.PICKUP ? (
              <LocationModal isPick />
            ) : (
              <div onClick={() => openLocationModal()}>Check delivery address ZIP CODE</div>
            )}
        </DeliveryMethods>
        }
      />
  </SubHeaderWrapper>
  );

};

export default SubHeader;
