import React, { useState, useEffect, useRef, useContext } from 'react';
import { FormattedMessage, useIntl } from 'react-intl';
import { DeliveryMethodsConstants, deliveryMethodCookieKeyName, resistenciaZipCode } from 'utils/constant';
import { useQuery } from '@apollo/react-hooks';
import { DELIVERY_METHOD } from 'graphql/query/delivery';
import { GiftBox } from 'assets/icons/GiftBox';
import Checkbox from 'components/checkbox/checkbox';
import { getCookie, setCookie } from 'utils/session';
import { CardWrapper, Container, DeliveryMethods, DeliveryText, Heading, Input, Offer, OfferSection, Options, PickUpOptions, Wrapper } from './delivery-selection.style';
var classifyPoint = require("robust-point-in-polygon")
import PlacesAutocomplete, { geocodeByAddress, getLatLng } from "react-places-autocomplete";

interface Props {
  deliveryMethodSaved: any
  setDeliveryMethodSaved: any
}

const DeliverySelection: React.FC<Props> = ({ ...props  }) => {
  const [deliveryMethodTypeSelected, setDeliveryMethodType] = React.useState();

  const [deliveryMethodSelected, setDeliveryMethod] = React.useState(undefined);
  const { data: deliverData } = useQuery(DELIVERY_METHOD)
  const deliveryMethods = deliverData?.deliveryMethods?.items;
  const [zipCode, setZipCode] = React.useState('');
  const [searchResult, setSearchResult] = React.useState([]);
  const [deliveryAddress, setDeliveryAddress] = useState("");

  // useEffect(() => {
  //   if (getCookie(deliveryMethodCookieKeyName) && !props.deliveryMethodSaved) {
  //     const deliveryMethodSaved = JSON.parse(getCookie(deliveryMethodCookieKeyName));
  //     props.setDeliveryMethodSaved(deliveryMethodSaved)
  //   }
  // }, [props.deliveryMethodSaved]);

  const setDeliveryMethodAndSaveCookie =  (deli) => {
    setDeliveryMethod(deli)
    props.setDeliveryMethodSaved(deli)
    // if (getCookie('first_visit')) {
    setCookie(deliveryMethodCookieKeyName, deli);
    // }
  };

  function inside(point, vs) {
    var x = point[0], y = point[1];

    var inside = false;
    for (var i = 0, j = vs.length - 1; i < vs.length; j = i++) {
        var xi = vs[i][0], yi = vs[i][1];
        var xj = vs[j][0], yj = vs[j][1];

        var intersect = ((yi > y) != (yj > y))
            && (x < (xj - xi) * (y - yi) / (yj - yi) + xi);
        if (intersect) inside = !inside;
    }

    return inside;
};

  const searchPickupZipCode =  (e) => {
    const zipCode = e.target.value;
    handleOnFocus(e)
    setZipCode(zipCode)
    const pickUpOptionsMethods = deliveryMethods?.filter(deliveryMethod => {
      return deliveryMethod.isPickUp === true;
    });

    // const methodFound = pickUpOptionsMethods.filter(method => method.details.includes(zipCode)
    const methodFound = zipCode === resistenciaZipCode ? pickUpOptionsMethods : [];

    // e.stopPropagation();
    setSearchResult(methodFound.length > 0 ? methodFound : null)

    // setSearchResult(pickUpOptionsMethods)
  };

  const setDelivery =  (e, deli: DeliveryMethodsConstants) => {
    e.stopPropagation();
    setDeliveryMethodType(deli)
    setSearchResult(null)
    setZipCode('')
  };

  const handleOnFocus =  (event) => {
    event.preventDefault()
    event.stopPropagation()
  };

  // deliveryMethodSelected
  const isPickUpSelected = deliveryMethodTypeSelected === DeliveryMethodsConstants.PICKUP;
  const isDeliverySelected = deliveryMethodTypeSelected === DeliveryMethodsConstants.DELIVERY;
  const intl = useIntl();

  const plazadoceDeOctubrePolygon = [
    [-27.438348, -58.972117],
    [-27.451195, -58.986501],
    [-27.441537, -58.997427],
    [27.428763, -58.983112],
  ]

  const plazaBelgranoPolygon = [
    [-27.4481, -58.96074],
    [-27.43829, -58.9722],
    [-27.45114, -58.98647],
    [-27.46134, -58.97495],
  ]

  const plazaNueveDeJulioPolygon = [
    [-27.45114, -58.98647],
    [-27.46134, -58.97495],
    [-27.47423, -58.98923],
    [-27.46392, -59.00075],
  ]

  const plazaEspañaPolygon = [
    [-27.46392, -59.00075],
    [-27.45113, -58.9865],
    [-27.45442, -59.01161],
    [-27.44148, -58.99731],
  ]

  const handleSelect = async (address) => {
    setDeliveryAddress(address);
    const results = await geocodeByAddress(address);
    const latLng = await getLatLng(results[0]);
    let deliveryOptionsMethods = [];

    if (inside([latLng.lat, latLng.lng], plazadoceDeOctubrePolygon)) {
      deliveryOptionsMethods = deliveryMethods?.filter(deliveryMethod => {
        if (!deliveryMethod.isPickUp && deliveryMethod.details.includes('Lunes')) { deliveryMethod.name = 'Delivery Gratis'; return true;}
        return !deliveryMethod.isPickUp;
      });
    } else if (inside([latLng.lat, latLng.lng], plazaBelgranoPolygon)) {
      deliveryOptionsMethods = deliveryMethods?.filter(deliveryMethod => {
        if (!deliveryMethod.isPickUp && deliveryMethod.details.includes('Martes')) { deliveryMethod.name = 'Delivery Gratis'; return true;}
        return !deliveryMethod.isPickUp;
      });
    } else if (inside([latLng.lat, latLng.lng], plazaNueveDeJulioPolygon)) {
        deliveryOptionsMethods = deliveryMethods?.filter(deliveryMethod => {
          if (!deliveryMethod.isPickUp && deliveryMethod.details.includes('Miercoles')) { deliveryMethod.name = 'Delivery Gratis'; return true;}
          return !deliveryMethod.isPickUp;
        });
    } else if (inside([latLng.lat, latLng.lng], plazaEspañaPolygon)) {
      deliveryOptionsMethods = deliveryMethods?.filter(deliveryMethod => {
        if (!deliveryMethod.isPickUp && deliveryMethod.details.includes('Jueves')) { deliveryMethod.name = 'Delivery Gratis'; return true;}
        return !deliveryMethod.isPickUp;
      });
    }
    setSearchResult(deliveryOptionsMethods)
  };


  // const searchDeliveryZipCode =  (e) => {
  //   handleOnFocus(e)
  //   setZipCode(e.target.value)


  //   const methodFound = deliveryOptionsMethods.filter(method => method.name.includes(zipCode));
  //   e.stopPropagation();
  //   // setSearchResult(deli ? methodFound : null)
  //   setSearchResult(deliveryOptionsMethods)
  // };


  return (
        <DeliveryMethods>
            <Options>
                <CardWrapper color={isPickUpSelected ? '#009E7F' : '#e4f4fc'} onClick={(e) => setDelivery(e, DeliveryMethodsConstants.PICKUP)}><FormattedMessage id="pickUpId" defaultMessage="notFoundId" /></CardWrapper> 
                <CardWrapper color={isDeliverySelected ? '#009E7F' : '#e4f4fc'} onClick={(e) => setDelivery(e, DeliveryMethodsConstants.DELIVERY)}><FormattedMessage id="deliveryId" defaultMessage="notFoundId2" /></CardWrapper> 
            </Options>
            <Wrapper>

                { !!deliveryMethodTypeSelected && (
                  <Container>
                    <Heading  onClick={(e) => handleOnFocus(e)}>
                        { isPickUpSelected ? (
                        <FormattedMessage
                            id="pickupModalheading"
                            defaultMessage="pickupModalheading"
                        />
                        ) : (
                        <FormattedMessage
                            id="deliveryModalheading"
                            defaultMessage="deliveryModalheading"
                        />
                        )}
                      </Heading>
                      { isPickUpSelected ? (
                        <div onClick={(e) => handleOnFocus(e)}>
                          <Input
                              type='text'
                              name='name'
                              onFocus={(e) => handleOnFocus(e)}
                              // placeholder='Código postal'
                              value={zipCode}
                              autoComplete="off"
                              // we have to change the onChange because the is no one for the controller name actualy
                              onChange={(e) => searchPickupZipCode(e)}
                          />
                        </div>
                        ) : (
                        <PlacesAutocomplete
                          value={deliveryAddress}
                          onChange={(e) => setDeliveryAddress(e)}
                          onSelect={handleSelect}
                          searchOptions={{
                            types: [],
                            componentRestrictions: { country: "ar" },
                          }}
                        >
                          {({ getInputProps, suggestions, getSuggestionItemProps, loading }) => (
                            <div onClick={(e) => handleOnFocus(e)}>
                              <input
                                {...getInputProps({
                                  placeholder: "Enter address",
                                  className: "location-search-input",
                                  style: {
                                    display: 'block',
                                    width: '220px',

                                    padding:'0px 8px',
                                    appearance: 'none',
                                    fontFamily: `'Lato', sans-serif`,
                                    fontSize: 'base',
                                    lineHeight: 'inherit',
                                    border: '1px solid',
                                    borderColor: '#f1f1f1',
                                    borderRadius: '3px',
                                    backgroundColor: 'white',
                                    color: 'text.bold',
                                    height: '48px',
                                    transition: 'all 0.25s ease',
                                    // mb: 3,
                                    '&:focus': {
                                      borderColor: '#009E7F',
                                    },
                                  },
                                })}
                              />
                              <div className="autocomplete-dropdown-container">
                                {loading && <div>Loading...</div>}
                                {suggestions.map((suggestion) => {
                                  const style = suggestion.active
                                    ? { backgroundColor: "#fafafa", cursor: "pointer" }
                                    : { backgroundColor: "#ffffff", cursor: "pointer" };
                                  return (
                                    <div {...getSuggestionItemProps(suggestion, { style })}>
                                      {suggestion.description}
                                    </div>
                                  );
                                })}
                              </div>
                            </div>
                          )}
                        </PlacesAutocomplete>
                        )}
                      {searchResult?.length ? searchResult.map((result, i) => {
                          return (
                            <PickUpOptions>
                              <Checkbox
                                  keyName={`${i}-results`}
                                  isChecked={deliveryMethodSelected?.id === result.id}
                                  labelText={`${result.name} - ${result.details} `}
                                  id={`result-${i}`}
                                  onChange={e => {
                                      setDeliveryMethodAndSaveCookie(deliveryMethodSelected?.id === result.id ? null : result)
                                  }}
                              />
                            </PickUpOptions>
                          )}
                          ).sort() : ('')
                      }
                      { !searchResult && zipCode.length >= 4 && deliveryMethodTypeSelected === DeliveryMethodsConstants.PICKUP && (
                        <DeliveryText>{intl.formatMessage({ id: 'noDittoPickUpLocations', defaultMessage: 'noDittoPickUpLocations' })}</DeliveryText>
                      )}
                      {!searchResult?.length && !deliveryAddress.length && (
                        <DeliveryText>{intl.formatMessage({ id: 'noDeliveryThereYet', defaultMessage: 'noDeliveryThereYet' })}</DeliveryText>
                      )}
                  </Container>
                )}
                <OfferSection>
                <GiftBox />
                <Offer>
                    <FormattedMessage
                    id="locationModalFooter"
                    defaultMessage="Free Delivery For 1st Order"
                    values={{ number: 1 }}
                    />
                </Offer>
                </OfferSection>
            </Wrapper>
        </DeliveryMethods>
  );

};

export default DeliverySelection;
