import React, { useState, useEffect, useRef } from 'react';
import { BannerIcon, BannerText, LocationContent, SubHeaderWrapper } from './sub-header.style';
import { useLocale } from 'contexts/language/language.provider';
import DeliveryIcon from 'assets/images/locationIcon.webp';
import Popover from 'components/popover/popover';
interface Props {
  className: string
}


const LocationMenu = ({ cityAndStateText, onClick }) => {
  return (
    <LocationContent onClick={onClick}>
        <BannerIcon>
          <img src={DeliveryIcon} alt="" />
        </BannerIcon>
        <BannerText>
          {cityAndStateText && cityAndStateText}
        </BannerText>
      </LocationContent>
  );
};

const DeliveryMethodsView = ({ cityAndStateText, onClick }) => {
  return (
    <LocationContent onClick={onClick}>
        <BannerIcon>
          <img src={DeliveryIcon} alt="" />
        </BannerIcon>
        <BannerText>
          {cityAndStateText && cityAndStateText}
        </BannerText>
      </LocationContent>
    // <>
    //   <MenuItem onClick={onClick} key={item.id} value={item.id}>
    //     <span>
    //       <FlagIcon name={item.icon} />
    //     </span>
    //     <FormattedMessage id={item.id} defaultMessage={item.defaultMessage} />
    //   </MenuItem>
    // </>
  );
};

const SubHeader: React.FC<Props> = ({ ...props  }) => {
  const { location } = useLocale();
  const cityAndStateText = location ? `${location.split(', ')[1]}, ${location.split(', ')[2]}` : '';

  return (
    <SubHeaderWrapper className={props.className} id="layout-header">
      <Popover
        direction="left"
        className="user-pages-dropdown"
        handler={<LocationMenu onClick={()=> null} cityAndStateText={cityAndStateText} />}
        content={<DeliveryMethodsView onClick={()=> null} cityAndStateText={cityAndStateText} />}
      />
  </SubHeaderWrapper>
  );

};

export default SubHeader;
