          <ListDes>
            <Select 
              onChange={(e: any) => handleLightSettingChange(plant, 'relayOneIdRelated', e.value)}
              value={relayOneSelected}
              options={fourRelaysOptions}
              styles={selectStyle}
              menuPosition={'fixed'}
            />
          </ListDes>
        </ListItem>

        { setting.mode === LightSensorModes.MANUAL && (
          <>
            <ListItem>
              <ListTitle>
                <Text bold>
                  <FormattedMessage
                    id='manualModeStateId'
                    defaultMessage='manualModeStateId'
                  />
                </Text>
              </ListTitle>
              <ListDes>
                <Select 
                  onChange={(e: any) => handleLightSettingChange(plant, 'relayOneWorking', e.value)}
                  value={selectedManualState}
                  options={manualModeOptions}
                  styles={selectStyle}
                  menuPosition={'fixed'}
                />
              </ListDes>
            </ListItem>
          </>
        )}

        { setting.mode === LightSensorModes.SCHEDULE && (
          <>
            <WeekContainer>
            {Object.keys(WeekDays).map((day, i: number) => {
               return (
                  <DayContainer
                    key={i + '-day-container'}
                    style={{ backgroundColor: daySelected === day ? '#E6E6E6' : 'transparent' }}
                    onClick={() => setDay(day)}
                  >
                    {day.substring(0,3)}
                  </DayContainer>
                )
              })
            }
            </WeekContainer>

            <Button
              size='big'
              variant='outlined'
              type='button'
              className='add-button'
              onClick={() =>handleModal(
                  AddTimeSchedule, 
                  {
                    item:{
                      name: ''
                    },
                    id: data?.getUser?.id
                  },
                  'add-address-modal')
              }
            >
              <FormattedMessage
                id='addTimeScheduleId'
                defaultMessage='addTimeScheduleId' 
              />
            </Button>
          </>
        )}


      </PlantsSensorContainer>

    )
  }

  const selectStyle = {
    control: styles => ({ ...styles, width: '197px' }),
    // option: (styles, { data, isDisabled, isFocused, isSelected }) => {
    //   // const color = chroma(data.color);
    //   return {
    //     ...styles,
    //     backgroundColor: isDisabled ? 'red' : blue,
    //     color: '#FFF',
    //     cursor: isDisabled ? 'not-allowed' : 'default',
    //     ...
    //   };
    // }
  };

  return (
    <PlantPageWrapper>
      <PlantsPageContainer style={{ width: '100%' }}>
        <Link href="/profile">
          <a className="home-btn">
            <FormattedMessage id="backProfileBtn" defaultMessage="Back to Profile" />
          </a>
        </Link>

        <OrderDetails>
          <BlockTitle>
            <FormattedMessage
              id="yourPlants"
              defaultMessage="Your Plants"
            />
          </BlockTitle>

          { plants?.length < 1 && (<Text>No tienes plantas registradas</Text>) }
          { plants?.map((plant, i: number) => {
              return (
                <PlantsWrapper key={i + '-orderList'}>
                  <ListItem>
                      <ListTitle>
                        <Text bold>
                          <FormattedMessage
                            id="controllerNameId"
                            defaultMessage="controllerNameId"
                          />
                        </Text>
                      </ListTitle>
                      <ListDes>
                        <Input
                          type='text'
                          name='name'
                          value={plant?.name}
                          // onChange={(e: any) => handleHumiditySettings1Change(plant, 'name', e.target.value)}
                          backgroundColor='#F7F7F7'
                          width='197px'
                        />
                      </ListDes>
                    </ListItem>

                  {renderSoilSensor1(plant, plant.soilHumiditySettings1, i)}
                  {renderSoilSensor2(plant, plant.soilHumiditySettings2, i)}
                  {renderLightSensor(plant, plant.lightSettings, i)}
                  {renderAirTemperatureSensor(plant, i)}
                  {renderAirHumiditySensor(plant, i)}

                </PlantsWrapper>
              )
            })
          }

          {userinfoMsg && (
              <SuccessMsg>
                <FormattedMessage
                  id='userInfoSuccess'
                  defaultMessage={userinfoMsg}
                />
              </SuccessMsg>
          )}

        </OrderDetails>
      </PlantsPageContainer>

      <PlantsPageContainer style={{ width: '100%' }}>
        <BlockTitle>
          <FormattedMessage
            id="addController"
            defaultMessage="Your Plants"
          />
        </BlockTitle>

        <ListItem>
          <ListTitle>
            <Text bold>
              <FormattedMessage
                id='plantNameField'
                defaultMessage='Name of the plant'
              />
            </Text>
          </ListTitle>
          <ListDes>
            <Input
              type='text'
              name='name'
              value={name}
              onChange={(e) => setPlantName(e.target.value)}
              backgroundColor='#F7F7F7'
              width='197px'
            />
          </ListDes>
        </ListItem>

        <ListItem>
          <ListTitle>
            <Text bold>
              <FormattedMessage
                id='controllerIdField'
                defaultMessage='ID of the controller'
              />
            </Text>
          </ListTitle>
          <ListDes>
            <Input
              type='text'
              name='controllerId'
              value={controllerId}
              onChange={(e) => setControllerID(e.target.value)}
              backgroundColor='#F7F7F7'
              width='197px'
            />
          </ListDes>
        </ListItem>

        <Button className="cart-button" variant="secondary" borderRadius={100} onClick={handleAddClick}>
          <ButtonText>
            <FormattedMessage id={"addPlantButton"} defaultMessage="Add plant" />
          </ButtonText>
        </Button>
      </PlantsPageContainer>
    </PlantPageWrapper>
  );
};

export default YourPlants;
