import React,  { useState, useEffect } from 'react';
import Link from 'next/link';
import { useRouter } from 'next/router'
import moment from 'moment';
import { useQuery } from '@apollo/react-hooks';
import { GET_ORDERS } from 'graphql/query/order.query';
import { CURRENCY } from 'utils/constant';
import ErrorMessage from 'components/error-message/error-message';
import { Modal } from '@redq/reuse-modal';
import {Button} from "../components/button/button";
import OrderReceivedWrapper, {
  OrderReceivedContainer,
  OrderInfo,
  OrderDetails,
  TotalAmount,
  BlockTitle,
  Text,
  InfoBlockWrapper,
  InfoBlock,
  ListItem,
  ListTitle,
  ListDes,
} from './your-plants.style';
import { FormattedMessage, useIntl } from 'react-intl';
import { GET_LOGGED_IN_USER } from 'graphql/query/customer.query';
import { OrderListWrapper } from 'features/user-profile/order/order.style';
import ErrorMessageTwo from 'components/error-message/error-message-two';

type YourPlantsProps = {
  data?: any;
  index?: Number;
  Router?: any;
};

const YourPlants: React.FunctionComponent<YourPlantsProps> = (props) => {
  const router = useRouter();
  const intl = useIntl();
  const { data, error, loading } = useQuery(GET_LOGGED_IN_USER);
  if (loading) {
    return <ErrorMessage message={'Cargando...'} />
  };
  if (error?.toString()?.includes('no token sent')) {
        // @ts-ignore
        return (
            <>
                <ErrorMessageTwo message={intl.formatMessage({ id: 'notAuth', defaultMessage: 'notAuth' })}/>
                <LoginMessageDiv>
                    <Modal>
                    <Button
                        type='button'
                        onClick={toggleSignInForm}
                        size='big'
                        style={{width: '200px'}}
                    >
                        {intl.formatMessage({ id: 'loginBtnText', defaultMessage: 'loginBtnText' })}
                    </Button>
                    <Offer style={{ padding: '20px 20px' }}>
                        <FormattedMessage
                            id='dontHaveAccount'
                            defaultMessage="Don't have any account?"
                        />{' '}
                        <LinkButton onClick={toggleSignUpForm}>
                            <FormattedMessage id='signUpBtnText' defaultMessage='Sign Up' />
                        </LinkButton>
                    </Offer>
                    </Modal>
                </LoginMessageDiv>
            </>
        )
    }
  if (error) {
    return (
      <ErrorMessage message={error.message} />
    );
  };

  const printHandler = () =>{
    if (typeof window !== 'undefined') {
      window.print()
    }
  }
  // const dateAndTime = `${moment(myOrder?.datetime).format('MM/DD/YY')}, ${moment(myOrder?.datetime).format('hh:mm A')}`;
  const userPlants = data?.getUser?.plants;

  return (
    <OrderReceivedWrapper>
      <OrderReceivedContainer>
        <Link href="/profile">
          <a className="home-btn">
            <FormattedMessage id="backProfileBtn" defaultMessage="Back to Profile" />
          </a>
        </Link>

        <OrderDetails>
          <BlockTitle>
            <FormattedMessage
              id="yourPlants"
              defaultMessage="Your Plants"
            />
          </BlockTitle>

          { userPlants?.length < 1 && (<Text>No tienes plantas registradas</Text>) }
          { userPlants?.map((plant) => {
              return (
                <OrderListWrapper>
                  <ListItem>
                    <ListTitle>
                      <Text bold>
                        <FormattedMessage
                          id="plantName"
                          defaultMessage="plantName"
                        />
                      </Text>
                    </ListTitle>
                    <ListDes>
                      <Text>{plant?.name} </Text>
                    </ListDes>
                  </ListItem>

                  <ListItem>
                    <ListTitle>
                      <Text bold>
                        <FormattedMessage
                          id="tempId"
                          defaultMessage="tempId"
                        />
                      </Text>
                    </ListTitle>
                    <ListDes>
                      <Text>{plant?.temperatura} </Text>
                    </ListDes>
                  </ListItem>

                  <ListItem>
                    <ListTitle>
                      <Text bold>
                        <FormattedMessage
                          id="humedadId"
                          defaultMessage="humedadId"
                        />
                      </Text>
                    </ListTitle>
                    <ListDes>
                      <Text>{plant?.humedad} </Text>
                    </ListDes>
                  </ListItem>

                  <ListItem>
                    <ListTitle>
                      <Text bold>
                        <FormattedMessage
                          id="phId"
                          defaultMessage="PH"
                        />
                      </Text>
                    </ListTitle>
                    <ListDes>
                      <Text>{"7.2"} </Text>
                    </ListDes>
                  </ListItem>
                </OrderListWrapper>
              )
            })
          }

        </OrderDetails>
      </OrderReceivedContainer>
    </OrderReceivedWrapper>
  );
};

export default YourPlants;
