export const getDeliveryOrPickUpDatetime = (detailsText: string) => {
    let content = ifHorarioWordFoundDeleteIt(detailsText);
    const contentDivided = content?.split(' | ');
    return `${contentDivided[1] && contentDivided[1]} ${contentDivided[2] && contentDivided[2]}`;
}

const ifHorarioWordFoundDeleteIt = (details) => {
    if (!details) return '';
    const word = 'Horario: ';

    const index = details.indexOf(word);   // 8
    const length = word.length;			// 7

    return details.slice(index + length);
  }
