import React, { useState } from 'react';
import {
  StyledCheckBox,
  StyledCheckBoxLabel,
  StyledCheckBoxLabelText,
  StyledCheckBoxInput,
  StyledCheckBoxInputIndicator,
} from './checkbox.style';

type CheckBoxProps = {
  id: string;
  disabled?: boolean;
  isChecked?: boolean;
  labelText: string;
  className?: string;
  labelPosition?: 'left' | 'right';
  [key: string]: unknown;
  onChange: any
};

const CheckBox: React.FC<CheckBoxProps> = ({
  className,
  isChecked,
  labelText,
  id,
  labelPosition = 'right',
  disabled = false,
  onChange,
}) => {
  // use toggle hooks
  const [checked, setChecked] = useState(isChecked);
  console.log('isChecked', isChecked)
  return (
    <StyledCheckBox className={`Mahdi Fashion__checkbox ${className}`.trim()}>
      <StyledCheckBoxLabel htmlFor={id} position={labelPosition}>
        {labelText && (
          <StyledCheckBoxLabelText position={labelPosition}>
            {labelText}
          </StyledCheckBoxLabelText>
        )}

        <StyledCheckBoxInputIndicator />
      </StyledCheckBoxLabel>
    </StyledCheckBox>
  );
};

export default CheckBox;
