import React,  { useState, useEffect, useContext } from 'react';
import Link from 'next/link';
import { useMutation, useQuery } from '@apollo/react-hooks';
import ErrorMessage from 'components/error-message/error-message';

import {
  PlantsPageContainer,
  PlantPageWrapper,
} from './ditto-market.style';
import { FormattedMessage, useIntl } from 'react-intl';
import { GET_LOGGED_IN_USER, GET_LOGGED_IN_USER_SETTINGS } from 'graphql/query/customer.query';
import { ProfileContext } from 'contexts/profile/profile.context';
import { AuthContext } from 'contexts/auth/auth.context';
import ReactMapGL from "react-map-gl";

type DittoMarketProps = {
  data?: any;
  userRefetch: any;
  index?: Number;
  Router?: any;
  deviceType?: {
    mobile: boolean;
    tablet: boolean;
    desktop: boolean;
  };
};

const DittoMarket: React.FC<DittoMarketProps> = ({ deviceType, userRefetch }) => {
  const { state, dispatch } = useContext(ProfileContext);
  const {authDispatch} = useContext<any>(AuthContext);

  const { loading, error, data = {} } = useQuery(GET_LOGGED_IN_USER, {
    notifyOnNetworkStatusChange: true,
    fetchPolicy: "network-only",
    // pollInterval: 5000,
  });

  const [viewport, setViewport] = useState({
    height: "100%",
    width: "100%"
  });

  const intl = useIntl();

  if (loading) {
    return <ErrorMessage message={'Cargando...'} />
  };

  if (error) {
    return (
      <ErrorMessage message={error.message} />
    );
  };

  return (
    <PlantPageWrapper>
      <PlantsPageContainer style={{ width: '100%' }}>
        <Link href="/profile">
          <a className="home-btn">
            <FormattedMessage id="backProfileBtn" defaultMessage="Back to Profile" />
          </a>
        </Link>
        <Map
          style="mapbox://styles/mapbox/streets-v9"
          containerStyle={{
            height: '100vh',
            width: '100vw'
          }}
        >
          <Layer type="symbol" id="marker" layout={{ 'icon-image': 'marker-15' }}>
            <Feature coordinates={[-0.481747846041145, 51.3233379650232]} />
          </Layer>
        </Map>
      </PlantsPageContainer>
    </PlantPageWrapper>
  );
};

export default DittoMarket;
