import { GET_LOGGED_IN_USER_SETTINGS } from 'graphql/query/customer.query';

interface Props {
  module: any;
  plantId: any;
}

const LightReading: React.FC<Props> = ({ module, plantId  }) => {

      const plantIndex = latestDataFetched?.getUser?.plants?.findIndex((plant: any) => plant.plantId === plantId);
      const sensorIndex = latestDataFetched?.getUser.plants[plantIndex]?.sensors?.findIndex((sensor: any) => sensor.settingType === module.settingType);            
      let reading = latestDataFetched?.getUser.plants[plantIndex]?.sensors[sensorIndex]?.reading;

    return (
        <Text bold>{reading} % {reading < 40 ? 'ðŸŒ™' : 'ðŸ’¡'}</Text>
    );
};

export default LightReading;
