import styled from 'styled-components';
import { themeGet } from '@styled-system/theme-get';

export const Wrapper = styled.label`
  cursor: pointer;
  position: relative;
  /* display: block; */
  display: flex;
  padding: 0 10px;
  width: 100%;
  > input {
    position: absolute;
    top: 0;
    left: 0;
    opacity: 0;
    &:checked {

    }
  }
  &:hover {
    .card-remove-btn {
      opacity: 1;
    }
  }
`;
export const PaymentCardWrapper = styled.span`
  display: flex;
  width: 100%;
  flex-direction: column;
  position: relative;
  background-color: ${(props) => props.color || '#e4f4fc'};
  padding: 15px 20px;
  border-radius: ${themeGet('radii.base', '6px')};
  border-width: 2px;
  border-style: solid;
  border-color: ${(props) => props.color || '#e4f4fc'};
`;

export const CardLogo = styled.span`
  margin-bottom: 17px;
  display: block;
  img {
    max-width: 100%;
    height: 14px;
    display: block;
  }
`;

export const CardNumber = styled.span`
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 9px;
  span {
    font-size: calc(${themeGet('fontSizes.base', '15')}px - 1px);
    font-weight: ${themeGet('fontWeights.bold', '700')};
    color: ${themeGet('colors.text.bold', '#0D1136')};
    line-height: 1.25;
    &.card-number {
      font-size: ${themeGet('fontSizes.xs', '12')}px;
    }
  }
`;

export const CardNumTitle = styled.span`
  font-size: calc(${themeGet('fontSizes.base', '15')}px - 5px);
  font-weight: ${themeGet('fontWeights.regular', '400')};
  color: ${themeGet('colors.text.regular', '#77798C')};
  display: block;
  margin-bottom: 11px;
`;

export const Name = styled.span`
  font-size: ${themeGet('fontSizes.xs', '12')}px;
  font-weight: ${themeGet('fontWeights.bold', '700')};
  color: ${themeGet('colors.text.bold', '#0D1136')};
  display: block;
`;
export const DeleteButton = styled.span`
  display: flex;
  align-items: center;
  justify-content: center;
  border: 0;
  width: 18px;
  height: 18px;
  border-radius: 50%;
  overflow: hidden;
  cursor: pointer;
  outline: 0;
  padding: 0;
  color: #fff;
  background-color: ${themeGet('colors.secondary.regular', '#ff5b60')};
  position: absolute;
  top: 10px;
  right: 20px;
  z-index: 9;
  opacity: 0;
  transition: 0.2s ease-in-out;

  svg {
    display: block;
    width: 8px;
    height: auto;
  }
`;

export const CardTitle = styled.span`
  font-family: ${themeGet('fonts.body', 'Lato')};
  font-size: ${themeGet('fontSizes.sm', '13')}px;
  font-weight: ${themeGet('fontWeights.bold', '700')};
  color: ${themeGet('colors.text.bold', '#0D1136')};
  line-height: 1.2;
  margin-bottom: 5px;
  text-transform: capitalize;
`;

export const CardContent = styled.span`
  font-family: ${themeGet('fonts.body', 'Lato')};
  font-size: ${themeGet('fontSizes.base', '15')}px;
  font-weight: ${themeGet('fontWeights.regular', '400')};
  color: ${themeGet('colors.text.medium', '#424561')};
`;
