import React, { useState, useEffect, useRef } from 'react';
import { BannerIcon, BannerText, SubHeaderWrapper } from './sub-header.style';
import { useLocale } from 'contexts/language/language.provider';
import DeliveryIcon from 'assets/images/locationIcon.webp';
interface Props {
  className: string
}

const SubHeader: React.FC<Props> = ({ ...props  }) => {
  const { location } = useLocale();
  const cityAndStateText = location ? `${location.split(', ')[1]}, ${location.split(', ')[2]}` : '';

  return (
    <SubHeaderWrapper className={props.className} id="layout-header">
      <>
        <BannerIcon>
          <img src={DeliveryIcon} alt="" />
        </BannerIcon>
        <BannerText>
          {cityAndStateText && cityAndStateText}
        </BannerText>
      </>
  </SubHeaderWrapper>
  );

};

export default SubHeader;
