export const getDeliveryOrPickUpDatetime = (detailsText: string, delivery_date: string) => {

    const timeText = geTimeScheduleOnly(detailsText);

    const dateFrameText = geTimeScheduleOnly(detailsText);

    return `${delivery_date} ${timeFrameText}`;
}

// const deliveryDateAndTime = `${getDeliverySchedule(deliveryOptionSelected?.details)} - ${moment(deliveryDate).format('DD MMM')}`;

const geTimeScheduleOnly = (details) => {
    if (!details) return '';
    const word = 'Horario: ';

    const index = details.indexOf(word);   // 8
    const length = word.length;			// 7

    return details.slice(index + length);
  };
