import styled from 'styled-components';
import { Row as Rows } from 'react-styled-flexboxgrid';
import { themeGet } from '@styled-system/theme-get';
import { compose, layout, space, color, border } from 'styled-system';
import css from '@styled-system/css';
type TextProps = {
  bold?: any;
};

const OrderReceivedWrapper = styled.div`
  background-color: ${themeGet('colors.gray.200', '#f7f7f7')};
  position: relative;
  padding: 100px 0 60px 0;
  min-height: 100vh;
`;

export const PlantPageWrapper = styled.div`
  .dropdown-select__control.dropdown-select__control--is-focused {
    font-size: 16px !important;
  }

  background-color: ${themeGet('colors.gray.200', '#f7f7f7')};
  position: relative;
  // padding: 100px 0 60px 0;
  min-height: 100vh;
`;

export const InputUpper = styled.input<any>(
  css({
    display: 'block',
    width: '100%',
    textTransform: 'capitalize',
    p: '0 12px',
    appearance: 'none',
    fontFamily: 'body',
    fontSize: 'base',
    lineHeight: 'inherit',
    border: '1px solid',
    borderColor: 'gray.500',
    borderRadius: 'base',
    backgroundColor: 'white',
    color: 'text.bold',
    transition: 'all 0.25s ease',
    // mb: 3,
    '&:focus': {
      borderColor: 'primary.regular',
    },
  }),
  {
    '&:hover,&:focus': {
      outline: 0,
    },

    '&::placeholder': {
      color: '',
    },
    '&::-webkit-inner-spin-button,&::-webkit-outer-spin-button': {
      '-webkit-appearance': 'none',
      margin: 0,
    },
    '&.disabled': {
      cursor: 'not-allowed',
      opacity: 0.6,
    },
  },
  compose(layout, space, color, border)
);

export const NumberInput = styled.input<any>(
  css({
    display: 'block',
    width: '45px',
    textTransform: 'capitalize',
    p: '0 12px',
    appearance: 'none',
    fontFamily: 'body',
    fontSize: 'base',
    lineHeight: 'inherit',
    border: '1px solid',
    borderColor: 'gray.500',
    borderRadius: 'base',
    backgroundColor: 'white',
    color: 'text.bold',
    transition: 'all 0.25s ease',
    // mb: 3,
    '&:focus': {
      borderColor: 'primary.regular',
    },
  }),
  {
    '&:hover,&:focus': {
      outline: 0,
    },

    '&::placeholder': {
      color: '',
    },
    '&::-webkit-inner-spin-button,&::-webkit-outer-spin-button': {
      '-webkit-appearance': 'none',
      margin: 0,
    },
    '&.disabled': {
      cursor: 'not-allowed',
      opacity: 0.6,
    },
  },
  compose(layout, space, color, border)
);

export const ButtonText = styled.span`
/* @media (max-width: 767px) {
  display: none;
} */
`;

export const PlantsPageWrapper = styled.div`
  width: 100%;
  height: auto;
  min-height: 100vh;
  display: flex;
  flex-wrap: wrap;
  background-color: ${themeGet('colors.white', '#ffffff')};
  padding: 70px 0px 0px;

  // @media only screen and (max-width: 990px) {
  //   padding: 100px 0 60px;
  // }

  @media only screen and (min-width: 991px) and (max-width: 1280px) {
    padding: 130px 15px 60px;
  }
`;

export const SidebarSection = styled.div`
  width: 300px;
  flex-shrink: 0;
  margin-right: 30px;

  @media only screen and (max-width: 1199px) {
    display: none;
  }
`;

export const ContentBox = styled.div`
  width: calc(100% - 330px);
  height: auto;
  min-height: 200px;
  display: flex;
  flex-direction: column;
  padding: 60px 50px 20px;
  border: 1px solid ${themeGet('colors.gray.700', '#c3b2b2')};

  @media only screen and (max-width: 1199px) {
    width: 100%;
    border: 0;
    padding: 0px;
  }
`;

export const PlantsWrapper = styled.div`
  width: 100%;
  padding-left: 5px;
  height: auto;
  display: flex;
  flex-direction: column;
  flex-shrink: 0;
  overflow: hidden;
  border: 1px solid ${themeGet('colors.gray.900', '#c3b2b2')};
  border-radius: 5px;
  box-shadow: 0.5px 0.5px;
  margin: 5px;
  padding: 10px;

  @media only screen and (max-width: 1199px) {
    margin-right: 20px;
  }
`;

export const Column = styled.div`
  border: 1px solid ${themeGet('colors.gray.500', '#f1f1f1')};
  width: 100%;
  padding: 10px;
  height: auto;
  display: flex;
  flex-direction: column;
  flex-shrink: 0;
  margin-right: 30px;
  overflow: hidden;

  @media only screen and (max-width: 1199px) {
    margin-right: 20px;
  }
`;

export const PlantsSensorContainer = styled.button`
  background-color: #f7ecec;
  cursor: pointer;
  border: 1px solid ${themeGet('colors.gray.500', '#f1f1f1')};
  border-radius: ${themeGet('radii.base', '6px')};
  overflow: hidden;
  position: relative;
  // width: calc(100% - 30px);
  margin: 5px;
  width: fit-content;
  padding: 10px 5px;
  box-shaddow: 0.5px 0.5px;

  .delete-setting-btn {
    color: ${themeGet('colors.red', '#ffffff')};
    border-color: ${themeGet('colors.light_red', '#009e7f')};
    margin-left: 10px;
    margin-top: 20px;
    .btn-icon {
      margin-right: 5px;
    }

    svg {
      @media (max-width: 767px) {
        margin-right: 0;
      }
    }

    &:hover {
      color: ${themeGet('colors.white', '#ffffff')};
      background-color: ${themeGet('colors.red', '#c50707')};
      border-color: ${themeGet('colors.red', '#009e7f')};
    }
  }

`;

export const PlantsPageContainer = styled.div`
`;

export const OrderInfo = styled.div`
  margin-bottom: 60px;
  @media (max-width: 767px) {
    margin-bottom: 30px;
  }
`;

export const Row = styled(Rows)`
  margin-left: 0px;
`;


export const OrderDetails = styled.div`
  margin-bottom: 60px;
  @media (max-width: 767px) {
    margin-top: 50px;
    margin-bottom: 50px;
  }
`;

export const TotalAmount = styled.div``;

export const BlockTitle = styled.h2`
  font-family: ${themeGet('fonts.heading', 'sans-serif')};
  font-size: ${themeGet('fontSizes.lg', '21')}px;
  font-weight: ${themeGet('fontWeights.bold', '700')};
  color: ${themeGet('colors.text.bold', '#0D1136')};
  line-height: 1;
  margin-bottom: 32px;
  @media (max-width: 767px) {
    font-size: calc(${themeGet('fontSizes.base', '15')}px + 1px);
    margin-bottom: 25px;
  }
`;


export const CardButtons = styled.span`

  display: flex;
  width: 330px;
  margin-left: 100px;
  align-items: center;
  transition: 0.2s ease-in-out;
`;

export const ActionsButtons = styled.span`

  display: flex;
  align-items: center;
  transition: 0.2s ease-in-out;
`;

export const Type = styled.p<TextProps>`
  font-family: ${themeGet('fonts.body', 'Lato')};
  font-size: 10px;
  color: ${themeGet('colors.text.bold', '#0D1136')};
  // text-align: right;
  // padding: 0px 0px 0px 10px;
  width: 160px;

  font-weight: ${(props) =>
    props.bold
      ? themeGet('fontWeights.bold', '700')
      : themeGet('fontWeights.regular', '400')};
  line-height: 1.2;
  display: block;
  margin: 0;

  &:last-child {
    color: ${themeGet('colors.text.medium', '#424561')};
  }
`;

export const Text = styled.p<TextProps>`
  font-family: ${themeGet('fonts.body', 'Lato')};
  font-size: ${themeGet('fontSizes.base', '15')}px;
  color: ${themeGet('colors.text.bold', '#0D1136')};
  font-weight: ${(props) =>
    props.bold
      ? themeGet('fontWeights.bold', '700')
      : themeGet('fontWeights.regular', '400')};
  line-height: 1.2;
  display: block;
  margin: 0;

  &:last-child {
    color: ${themeGet('colors.text.medium', '#424561')};
  }
`;

export const TextSpaced = styled.p<TextProps>`
  font-family: ${themeGet('fonts.body', 'Lato')};
  font-size: ${themeGet('fontSizes.base', '15')}px;
  // color: ${themeGet('colors.text.bold', '#0D1136')};
  font-weight: ${(props) =>
    props.bold
      ? themeGet('fontWeights.bold', '700')
      : themeGet('fontWeights.regular', '400')};
  line-height: 1.2;
  display: block;
  margin-right: 10px;

  &:last-child {
    color: ${themeGet('colors.text.medium', '#424561')};
  }
`;

export const InfoBlockWrapper = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-top: 30px;
  @media (max-width: 767px) {
    flex-wrap: wrap;
  }
`;

export const InfoBlock = styled.div`
  flex-grow: 1;
  padding: 0 15px;
  border-left: 1px solid ${themeGet('colors.gray.500', '#f1f1f1')};
  @media (max-width: 767px) {
    max-width: 100%;
    flex: 0 0 100%;
    margin-bottom: 20px;
    padding: 0;
    border: 0;
  }

  &:first-child {
    padding-left: 0;
    border-left: 0;
  }
  &:last-child {
    padding-right: 0;
  }

  .title {
    margin-bottom: 10px;
  }
`;

export const ListItem = styled.div`
  display: flex;
  margin-bottom: 10px;
